import { useState } from "react"


const Index = () => {
  const [username, setUsername] = useState('')
  const [hobby, setHobby] = useState('')

  function updateUsername(ev){
    const {value} = ev.target
    setUsername(value)
  }

  function updateHobby(ev){
    const {value} = ev.target
    setHobby(value)
  }

  function handleSubmit(ev){
    ev.preventDefault()
    alert(`The username is ${username}, his hobby is ${hobby}`)
  }

  return(
    <div>
      <form onSubmit={handleSubmit}>
        username:<input type="text" value={username} onChange={updateUsername} /> <br/>
        hobby:<input type="text" value={hobby} onChange={updateHobby} /> <br/>
        <button>Submit</button>
      </form>
    </div>
  )
}

export default Index
